function htmlize(g,h){var d=g.nodeName.toLowerCase();var c="";if(d=="tbody"){h-=1}if(d=="#text"){c+=g.textContent}else{for(var f=0;f<h;f++){c+="  "}c+="<"+d;var e=$(g);var b={input:Array("type","name","id","checked"),td:Array("colspan"),label:Array("for")};if(b[d]!=undefined){for(f=0;f<b[d].length;f++){var a=b[d][f];if((e.attr(a)!=undefined)&&(e.attr(a)!="")){c+=" "+a+'="'+e.attr(a)+'"'}}}if((d=="input")&&(e.attr("type")!="radio")&&(e.attr("type")!="checkbox")&&(e.attr("value")!="")){c+=' value="'+e.attr("value")+'"'}if(d=="input"){return c+" />\n"}c+=">"}if((g.childNodes!=undefined)&&(g.childNodes.length>0)&&(g.childNodes[0].nodeName!="#text")){c+="\n"}for(var f=0;f<g.childNodes.length;f++){c+=htmlize(g.childNodes[f],h+1)}if(d!="#text"){if((g.childNodes!=undefined)&&(g.childNodes.length>0)&&(g.childNodes[0].nodeName!="#text")){for(f=0;f<h;f++){c+="  "}}c+="</"+d+">\n"}return c};